r\n		<#assign query=\"\">\r\n		<#if params??&&(params?size>0)>\r\n			<#assign query=\"?\">\r\n			<#assign  keys=params?keys/>\r\n			<#list keys as key>\r\n				<#if (key_index==0)>\r\n					<#assign query=query + key +\"=\" + params[key] >		\r\n				<#else>\r\n					<#assign query=query +\"&\" +key +\"=\" + params[key] >\r\n				</#if>\r\n			</#list>\r\n		</#if>\r\n</head>\r\n<body >   \r\n<div id=\"layout1\" class=\"mini-layout\" style=\"width:100%;height:100%;\">\r\n\r\n      <div region=\"center\"  title=\"数据源对话框\" showCollapseButton=\"false\" >\r\n     	<div class=\"mini-toolbar\" >\r\n	         <table style=\"width:100%;\">\r\n	             <tr>\r\n	                 <td style=\"width:100%;\">\r\n	                    <a class=\"mini-button\" iconCls=\"icon-search\"  onclick=\"onSearch()\">查询</a>\r\n                     	<a class=\"mini-button\" iconCls=\"icon-cancel\"  onclick=\"onClear()\">清空查询</a>\r\n                     	<span class=\"separator\"></span>\r\n                     	<a class=\"mini-button\" iconCls=\"icon-ok\"   onclick=\"CloseWindow(\'ok\');\">确定</a>\r\n		    			<a class=\"mini-button\" iconCls=\"icon-cancel\"  onclick=\"CloseWindow();\">取消</a>\r\n	                 </td>\r\n	             </tr>\r\n	              <tr>\r\n	                  <td  class=\"search-form\" style=\"white-space:nowrap;\">\r\n	                   	<div id=\"searchForm\">\r\n	                    	<div>\n <ul>\n  <li><span>数据源名称</span><input class=\"mini-textbox\" name=\"Q_NAME__S_LK\"></li>\n  <li><span>数据源名称</span><input class=\"mini-textbox\" name=\"Q_NAME__S_LK\"></li>\n </ul>\n</div>\r\n	                    </div>\r\n	                  </td>\r\n	              </tr>\r\n	         </table>\r\n	     </div>\r\n	    \r\n     	<div class=\"mini-fit rx-grid-fit\">\r\n			<div id=\"datagrid1\" class=\"mini-datagrid\" style=\"width: 100%; height: 100%;\" allowResize=\"false\" \r\n	        	url=\"${ctxPath}/dev/cus/customData/dataSourceDialog/getData.do${query}\" idField=\"ID_\" multiSelect=\"false\" showColumnsMenu=\"true\" \r\n	        	parentField=\"\" treeColumn=\"\" allowResize=\"false\" expandOnLoad=\"true\"\r\n	        	sizeList=\"[5,10,20,50,100,200,500]\" pageSize=\"20\" allowAlternating=\"true\">\r\n						<div property=\"columns\">\n <div type=\"indexcolumn\"></div>\n <div type=\"checkcolumn\"></div>\n <div header=\"数据源名称\" width=\"100\" field=\"NAME_\" name=\"NAME_\"></div>\n <div header=\"别名\" width=\"100\" field=\"ALIAS_\" name=\"ALIAS_\"></div>\n <div header=\"数据库类型\" width=\"100\" field=\"DB_TYPE_\" name=\"DB_TYPE_\"></div>\n</div>\r\n			</div>\r\n		</div>\r\n    </div>\r\n</div>\r\n\r\n    \r\n    <script type=\"text/javascript\">\r\n        mini.parse();\r\n		var grid=mini.get(\'datagrid1\');\r\n		grid.load();\r\n		var searchForm=new mini.Form(\'searchForm\');\r\n			\r\n		function onSearch(){\r\n			var data=searchForm.getData();\r\n			_ConvertFormData(data);\r\n			grid.load(data);\r\n		}\r\n		\r\n		//---------------开始生成服务器自定义按钮-------------\r\n		 //-------------结束生成服务器自定义按钮-------------\r\n		\r\n		 grid.on(\"drawcell\", function (e) {\r\n		   var record = e.record,\r\n		   field = e.field,\r\n		   value = e.value;\r\n		   if(field==\'CREATE_BY_\' || field==\'UPDATE_BY_\' || field==\'CREATE_USER_ID_\'){\r\n		     if(value){\r\n		     	e.cellHtml=\'<a class=\"mini-user\" iconCls=\"icon-user\" userId=\"\'+value+\'\"></a>\';\r\n		     }else{\r\n		     	e.cellHtml=\'<span style=\"color:red\">无</span>\';\r\n		     }\r\n		   }\r\n            \r\n            if( field== \"INST_STATUS_\"){\r\n            	var statusText=\"\";\r\n            	if(value==\'RUNNING\'){\r\n            		e.cellHtml=\'<span style=\"color:green\">运行中</span>\';\r\n            	}else if(value==\'DRAFTED\'){\r\n            		e.cellHtml=\'<span style=\"color:red\">草稿</span>\';\r\n            	}else if(value==\'SUCCESS_END\'){\r\n            		e.cellHtml=\'<span style=\"color:green\">成功结束</span>\';\r\n            	}else if(value==\'DISCARD_END\'){\r\n            		e.cellHtml=\'<span style=\"color:red\">作废</span>\';\r\n            	}else if(value==\'ABORT_END\'){\r\n            		e.cellHtml=\'<span style=\"color:red\">异常中止结束</span>\';\r\n            	}else if(value==\'PENDING\'){\r\n            		e.cellHtml=\'挂起\';\r\n            	}else{\r\n            		e.cellHtml=e.value;\r\n            	}\r\n            }\r\n            \r\n            \r\n            \r\n		 });\r\n		\r\n		 grid.on(\'update\',function(){\r\n		   _LoadUserInfo();\r\n		 });\r\n		  \r\n		 grid.on(\'rowdblclick\',function(e){\r\n		 	if(\'\'==\'\'){\r\n				return ;\r\n			}\r\n			var record=e.record;\r\n			\r\n			if(record==null){\r\n			   alert(\'请选择表格行\');\r\n			   return;\r\n			}\r\n			\r\n		   _OpenWindow({\r\n				title:\'数据源对话框--明细\',\r\n				height:400,\r\n				width:800,\r\n				max:true,\r\n				url:__rootPath+\'/sys/customform/sysCustomFormSetting/detail//\'+record.ID_+\'.do\',\r\n				ondestroy:function(action){\r\n					if(action!=\'ok\'){\r\n						return;\r\n					}\r\n				}\r\n			});\r\n		 });\r\n		 \r\n		function getUrlFromRecord(url,record){\r\n		\r\n            url=url.replace(\'{ctxPath}\',__rootPath);\r\n            for(var field in record){\r\n            	url=url.replace(\'{\'+field+\'}\',record[field]);\r\n            }\r\n            return url;\r\n		}\r\n		\r\n		function onClear(){\r\n			searchForm.clear();\r\n			var url=grid.getUrl();\r\n			var index=url.indexOf(\'?\');\r\n			if(index!=-1){\r\n				url=url.substring(0,index);\r\n			}\r\n			grid.setUrl(url);\r\n			grid.load();\r\n		}\r\n		\r\n		function onClose(){\r\n			CloseWindow();\r\n		}\r\n		\r\n		function onRemove(e){\r\n			var row=grid.getSelecteds();\r\n			if(row.length==0){\r\n			   alert(\'请选择表格行\');\r\n			   return;\r\n			}\r\n			\r\n			var url=\"/sys/customform/sysCustomFormSetting/remove/.do\";\r\n			if(e.sender && e.sender.url){\r\n				url=e.sender.url;\r\n			}\r\n			url=__rootPath+url;\r\n			mini.confirm(\"确定删除吗?\", \"提示信息\", function(action){\r\n                if (action != \"ok\")  return;\r\n				var ids = [];\r\n				for(var i=0; i < row.length; i++){\r\n					ids.push(row[i][\'ID_\']);\r\n				}\r\n				_SubmitJson({url:url,method:\"POST\",data:{id:ids.join(\',\')},success:function(){\r\n					grid.load();\r\n				}})    \r\n            })\r\n		}\r\n		\r\n		//返回选择的数据\r\n		function getData(){\r\n			var rows=grid.getSelecteds();\r\n			return rows;\r\n		}\r\n		\r\n		function showLink(value,field,url,linkType){\r\n			if(linkType==\'tabWindow\'){\r\n				top[\'index\'].showTabFromPage({\r\n        			tabId:\'_\'+field,\r\n        			title:value + \'-信息\',\r\n        			url:url\r\n        		});\r\n			}else if(linkType==\"newWindow\"){\r\n				 window.open(url); \r\n			}else{\r\n				 _OpenWindow({\r\n					title:value,\r\n					height:400,\r\n					width:800,\r\n					max:true,\r\n					url:url\r\n				});\r\n			}\r\n		}\r\n    </script>\r\n\r\n</body>\r\n</html>', '[{\"fc\":\"mini-textbox\",\"_id\":1,\"_uid\":1,\"_state\":\"added\",\"fieldLabel\":\"数据源名称\",\"dataType\":\"string\",\"queryDataType\":\"S\",\"fieldName\":\"NAME_\",\"fieldOpLabel\":\"%模糊匹配%\",\"fieldOp\":\"LK\",\"fcName\":\"文本框\",\"fieldText\":\"数据源名称\",\"type\":\"query\",\"typeName\":\"查询参数\",\"autoFilter\":\"YES\",\"autoFilterName\":\"是\"},{\"fc\":\"mini-textbox\",\"_id\":2,\"_uid\":2,\"_state\":\"added\",\"fieldLabel\":\"数据源名称\",\"dataType\":\"string\",\"queryDataType\":\"S\",\"fieldName\":\"NAME_\",\"fieldOpLabel\":\"%模糊匹配%\",\"fieldOp\":\"LK\",\"fcName\":\"文本框\",\"fieldText\":\"数据源名称\",\"type\":\"query\",\"typeName\":\"查询参数\",\"autoFilter\":\"YES\",\"autoFilterName\":\"是\"}]', '', '', '', '', 'YES', 'YES', '', 600, 800, '', 'YES', '1', '1', NULL, NULL, NULL, 'NO', NULL, '', '\r\n\r\n<yd-layout>\r\n	<yd-navbar slot=\"navbar\" title=\"数据源对话框\">\r\n      <a href=\"javascript:;\" v-on:click=\"goBack\" slot=\"left\">\r\n        <yd-navbar-back-icon></yd-navbar-back-icon>\r\n      </a>\r\n    </yd-navbar>\r\n    <rx-search slot=\"top\" v-model=\"search.Q_NAME__S_LK\" v-on:input=\"handSearch\"  :senior=\"true\"  v-on:showsenior=\"showSenior()\"  ></rx-search>\r\n    <!--参数-->\r\n    <rx-searchpanel v-on:search=\"handSearch\" ref=\"searchPanel\" >\r\n    	<div class=\"search-params\">\r\n    		<div class=\"title\">\r\n    			数据源名称\r\n    		</div>\r\n    		<div class=\"content\">\r\n    				<input  v-model=\"search.Q_NAME__S_LK\"  placeholder=\"请输入数据源名称\"/>\r\n    		</div>\r\n    	</div>\r\n    	<div class=\"search-params\">\r\n    		<div class=\"title\">\r\n    			数据源名称\r\n    		</div>\r\n    		<div class=\"content\">\r\n    				<input  v-model=\"search.Q_NAME__S_LK\"  placeholder=\"请输入数据源名称\"/>\r\n    		</div>\r\n    	</div>\r\n    </rx-searchpanel>\r\n    <!-- 列表字段 -->\r\n    <yd-pullrefresh :on-infinite=\"loadList\" ref=\"pullrefreshList_\">\r\n    		<div v-for=\"item in list\" v-if=\"list && list.length>0\" class=\"custom-list\">\r\n    			<div class=\"head\"   v-on:click=\"toggle(item)\"  >\r\n    				\r\n					<div class=\"head-content\">\r\n							<rx-radio name=\"selectId\" :val=\"item.ID_\" v-model=\"selectIds\">{{item.NAME_}}</rx-radio>\r\n					</div>\r\n	    			\r\n	    			<i  v-bind:class=\"{ rotated: item.expand }\"></i>\r\n	    		</div>\r\n	    		<div class=\"body\" v-show=\"item.expand\">\r\n	\r\n	    						<div class=\"body-item\">\r\n		    						<div class=\"title\">别名</div>\r\n			    					<div class=\"content\">\r\n			    						{{item.ALIAS_}}\r\n			    					</div>	\r\n		    					</div>\r\n	\r\n	    						<div class=\"body-item\">\r\n		    						<div class=\"title\">数据库类型</div>\r\n			    					<div class=\"content\">\r\n			    						{{item.DB_TYPE_}}\r\n			    					</div>	\r\n		    					</div>\r\n	\r\n	    		</div>\r\n    		</div>\r\n    </yd-pullrefresh>\r\n	<rx-nodata :amount=\"list.length\"></rx-nodata>\r\n	\r\n	<yd-tabbar slot=\"tabbar\">\r\n		<tabbar-item title=\"确定\" name=\"btnOk\" type=\"a\" v-on:handclick=\"clickOk()\"  icon=\"yd-icon-ok\" active=\"true\"></tabbar-item>\r\n		<tabbar-item title=\"取消\" name=\"btnClose\" type=\"a\" v-on:handclick